#ifndef CRYPTO3_ALGEBRA_FIELDS_MODP_SRP_HPP
#define CRYPTO3_ALGEBRA_FIELDS_MODP_SRP_HPP

#include <nil/crypto3/algebra/fields/detail/element/fp.hpp>

#include <nil/crypto3/algebra/fields/params.hpp>
#include <nil/crypto3/algebra/fields/field.hpp>

#include <nil/crypto3/detail/literals.hpp>

namespace nil {
    namespace crypto3 {
        namespace algebra {
            namespace fields {

                /*!
                 * @brief SRP groups
                 * SRP groups have a p st (p-1)/2 is prime, but g is not a generator
                 * of subgroup of size q, so set q == 0 to bypass generator check.
                 *
                 * Missing q doesn't matter for SRP, and nothing but SRP should be
                 * using these parameters.
                 *
                 * @tparam Version
                 */
                template<std::size_t Version>
                struct modp_srp : public field<Version> { };

                template<>
                struct modp_srp<1024> : public field<1024> {
                    typedef field<1024> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    typedef typename policy_type::extended_integral_type extended_integral_type;

                    constexpr static const std::size_t number_bits = policy_type::number_bits;
                    typedef typename policy_type::modular_type modular_type;

                    constexpr static const integral_type modulus =
                        0xEEAF0AB9ADB38DD69C33F80AFA8FC5E86072618775FF3C0B9EA2314C9C256576D674DF7496EA81D3383B4813D692C6E0E0D5D8E250B98BE48E495C1D6089DAD15DC7D7B46154D6B6CE8EF4AD69B15D4982559B297BCF1885C529F566660E57EC68EDBC3C05726CC02FD4CBF4976EAA9AFD5138FE8376435B9FC61D2FC0EB06E3_cppui1024;

                    constexpr static const integral_type mul_generator = 0x02;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                template<>
                struct modp_srp<1536> : public field<1536> {
                    typedef field<1536> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    constexpr static const integral_type modulus =
                        _cppui1536;

                    constexpr static const integral_type mul_generator = 0x02;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                template<>
                struct modp_srp<2048> : public field<2048> {
                    typedef field<2048> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    constexpr static const integral_type modulus =
                        _cppui2048;

                    constexpr static const integral_type mul_generator = 0x02;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                template<>
                struct modp_srp<3072> : public field<3072> {
                    typedef field<3072> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    constexpr static const integral_type modulus =
                        _cppui3072;

                    constexpr static const integral_type mul_generator = 0x05;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                template<>
                struct modp_srp<4096> : public field<4096> {
                    typedef field<4096> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    constexpr static const integral_type modulus =
                        _cppui4096;

                    constexpr static const integral_type mul_generator = 0x05;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                template<>
                struct modp_srp<6144> : public field<6144> {
                    typedef field<6144> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    constexpr static const integral_type modulus =
                        _cppui6144;

                    constexpr static const integral_type mul_generator = 0x05;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                template<>
                struct modp_srp<8192> : public field<8192> {
                    typedef field<8192> policy_type;

                    constexpr static const std::size_t modulus_bits = policy_type::modulus_bits;
                    typedef typename policy_type::integral_type integral_type;

                    constexpr static const integral_type modulus =
                        _cppui8192;

                    constexpr static const integral_type mul_generator = 0x13;

                    typedef typename detail::element_fp<params<modp_srp<modulus_bits>>> value_type;

                    constexpr static const std::size_t value_bits = modulus_bits;
                    constexpr static const std::size_t arity = 1;
                };

                constexpr typename modp_srp<1024>::integral_type const modp_srp<1024>::modulus;
                constexpr typename modp_srp<1536>::integral_type const modp_srp<1536>::modulus;
                constexpr typename modp_srp<2048>::integral_type const modp_srp<2048>::modulus;
                constexpr typename modp_srp<3072>::integral_type const modp_srp<3072>::modulus;
                constexpr typename modp_srp<4096>::integral_type const modp_srp<4096>::modulus;
                constexpr typename modp_srp<6144>::integral_type const modp_srp<6144>::modulus;
                constexpr typename modp_srp<8192>::integral_type const modp_srp<8192>::modulus;

                constexpr typename modp_srp<1024>::integral_type const modp_srp<1024>::mul_generator;
                constexpr typename modp_srp<1536>::integral_type const modp_srp<1536>::mul_generator;
                constexpr typename modp_srp<2048>::integral_type const modp_srp<2048>::mul_generator;
                constexpr typename modp_srp<3072>::integral_type const modp_srp<3072>::mul_generator;
                constexpr typename modp_srp<4096>::integral_type const modp_srp<4096>::mul_generator;
                constexpr typename modp_srp<6144>::integral_type const modp_srp<6144>::mul_generator;
                constexpr typename modp_srp<8192>::integral_type const modp_srp<8192>::mul_generator;

            }    // namespace fields
        }        // namespace algebra
    }            // namespace crypto3
}    // namespace nil

#endif    // CRYPTO3_ALGEBRA_FIELDS_MODP_SRP_HPP
